import UIKit
import GoogleMaps
struct PlaceDataInfo {
    var mainImage: UIImage
    var fullDescription: String
    var smallDescription: String
    var latitude: Double
    var longitude: Double
    var City: String
    var sightsName:String
    static func fetchSights() -> [PlaceDataInfo] {
        var cells = [PlaceDataInfo]()
        let Moscow1 = PlaceDataInfo(mainImage: UIImage(named: "Moscow1")!, fullDescription: "Кремль – это и древняя крепость, и вместилище прекрасных музеев и церквей, и резиденция президента Российской Федерации, и даже кладбище – в советские времена в кремлевской стене хоронили высших партийных деятелей. Протяженность кремлевских стен составляет 2,5 км, они укреплены 20 башнями, самой известной из которых является Спасская – именно на фоне ее курантов президент имеет обыкновение зачитывать россиянам свое новогоднее обращение.", smallDescription: "Московский Кремль – символ России (по крайней мере, для иностранцев).", latitude: 55.75202, longitude: 37.61749, City: "Moscow, Russia", sightsName:"Кремль")
        cells.append(Moscow1)
        let Moscow2 = PlaceDataInfo(mainImage: UIImage(named: "Moscow2")!, fullDescription: "Одна из самых красивых пешеходных улиц центра Москвы, получившая всемирную известность во время Чемпионата мира по футболу 2018 года. Именно здесь стихийно образовалась главная московская «фан-зона» — болельщики из разных стран на Никольской устраивали свои зажигательные перфомансы, радовались победам и заливали пивом горечь поражений.", smallDescription: "Тверска́я у́лица — крупнейшая улица Тверского района Центрального административного округа Москвы." , latitude: 55.75769, longitude: 37.62334, City: "Moscow, Russia", sightsName: "Никольская улица")
        cells.append(Moscow2)
        let Moscow3 = PlaceDataInfo(mainImage: UIImage(named: "Moscow3")!, fullDescription: "Старейший торговый дом страны – расположен на Красной площади и является не меньшей достопримечательностью Москвы, чем мавзолей или Исторический музей. Что в советские времена, что сейчас ГУМ считается торговым заведением класса «люкс» – раньше это было одно из немногих мест в стране, где был шанс нарваться на «дефицит», сейчас же его торговые ряды под завязку заполнены магазинами топовых брендов и авторскими шоу-румами.В последние годы хозяева (или арендаторы) активно эксплуатируют наследие недавнего социалистического прошлого – визуальный ряд, стилистика «а-ля 70-е», Муслим Магомаев из динамиков. И прогулка по ГУМу оставляет ощущение, что ты побывал в «СССР мечты, каким он никогда не был в реальности». ", smallDescription: "Старейший торговый дом страны – расположен на Красной площади и является не меньшей достопримечательностью Москвы, чем мавзолей или Исторический музей.", latitude: 55.75489, longitude: 37.62158, City: "Moscow, Russia", sightsName: "Государственный Универсальный Магазин")
        cells.append(Moscow3)
        let Moscow4 = PlaceDataInfo(mainImage: UIImage(named: "Moscow4")!, fullDescription: "Он был открыт в 1825 году спектаклем «Торжество муз» – и этот год принято считать годом основания театра, хотя театральная труппа к тому времени существовала уже полвека.Большой театр можно считать отличным индикатором жизни России – с момента своего основания он не раз горел, восстанавливался, находился в зените славы, затем снова горел и перестраивался. Как и вся страна.", smallDescription: "Государственный Академический Большой театр – крупнейший в России и один из самых известных и уважаемых театров в мире.", latitude: 55.76013, longitude: 37.61864, City: "Moscow, Russia", sightsName: "Большой Театр")
        cells.append(Moscow4)
        let Moscow5 = PlaceDataInfo(mainImage: UIImage(named: "Moscow5")!, fullDescription: "В его экспозиции представлено огромное количество сокровищ мировой культуры – античные скульптуры, артефакты Древнего Египта, произведения великих художников эпохи Возрождения.", smallDescription: "Будучи основан в 1912 году, изначально он назывался Музеем изящных искусств императора Александра III.", latitude: 55.7473, longitude: 37.60511, City: "Moscow, Russia", sightsName: "Пушкинский музей")
        cells.append(Moscow5)
        let Moscow6 = PlaceDataInfo(mainImage: UIImage(named: "Moscow6")!, fullDescription: "Первый храм с таким названием был построен на берегу Москвы-реки по распоряжению императора Александра I в честь победы над Наполеоном, но 5 декабря 1931 года он был взорван – советская власть решила, что помпезный Дом Советов с гигантской статуей Ленина на крыше будет уместнее. Но с Домом Советов не сбылось – помешала война, а после ее завершения было решено на месте недоделанного советского дворца построить открытый плавательный бассейн «Москва».Нынешний храм Христа Спасителя возвели на месте того бассейна в конце 90-х годов прошлого века – его внешний вид и внутреннее убранство полностью соответствуют взорванному храму.", smallDescription: "Самый большой в мире православный собор – в этом качестве он занесен в Книгу рекордов Гиннесса.", latitude: 55.74463, longitude: 37.60549, City: "Moscow, Russia", sightsName: "Храм Христа Спасителя")
        cells.append(Moscow6)
        let Moscow7 = PlaceDataInfo(mainImage: UIImage(named: "Moscow7")!, fullDescription: "Когда в 90-х на смену пафосу строителей коммунизма пришел цинизм рыночных отношений, парк Горького едва ли не в одночасье заполнили всевозможные шалманы-шашлычные, торговые ларьки, рекламные конструкции – а сам вход в парк сделали платным.", smallDescription: "Один из центров московской жизни и индикатор настроений общества.", latitude: 55.72849, longitude: 37.6001, City: "Moscow, Russia", sightsName: "Парк Горького")
        cells.append(Moscow7)
        let Moscow8 = PlaceDataInfo(mainImage: UIImage(named: "Moscow8")!, fullDescription: "Галерея была основана в 1892 году – когда Третьяков передал свою личную коллекцию в дар городу. На тот момент в ней насчитывалось более тысячи полотен живописи, порядка пятисот графических произведений, большая коллекция икон и несколько скульптур.", smallDescription: "Самое известное собрание живописи в России – названо по имени своего основателя Павла Третьякова, известного мецената и коллекционера предметов искусства.", latitude: 55.74138, longitude: 37.62086, City: "Moscow, Russia", sightsName: "Третьяковская галлерея")
        cells.append(Moscow8)
        let Moscow9 = PlaceDataInfo(mainImage: UIImage(named: "Moscow9")!, fullDescription: "Он должен был стать убежищем для высшего руководства страны в случае атомного или химического нападения – в бункере имелась полностью автономная система жизнеобеспечения. С появлением бомб, способных поражать подземные объекты, бункер утратил свое значение, был рассекречен и превращен в музей.", smallDescription: "Запасной командный пункт Таганский или «Бункер 42» – колоссальное подземное сооружение, построенное в середине прошлого века на случай перехода холодной войны в горячую. ", latitude: 55.74182, longitude: 37.64936, City: "Moscow, Russia", sightsName: "Музей холодной войны")
        cells.append(Moscow9)
        let Moscow10 = PlaceDataInfo(mainImage: UIImage(named: "Moscow10")!, fullDescription: "В планетарии можно, откинувшись в удобном кресле, изучать звездное небо, а можно посмотреть 4D фильмы о нашей Вселенной, узнать много интересного о мировом океане или вулкане Йеллоустоун.", smallDescription: "Самый современный планетарий в России – после масштабной реконструкции превратился в интереснейший развлекательно-образовательный комплекс.", latitude: 55.76144, longitude: 37.58365, City: "Moscow, Russia", sightsName: "Московский Планетарий")
        cells.append(Moscow10)
        let Kalin1 = PlaceDataInfo(mainImage: UIImage(named: "Kalin1")!, fullDescription: "Здесь же, на Центральной площади, можно обнаружить «здание-робот», оно же Дом Советов, «шедевр» советского панельного домостроения, чей внешний вид, видимо, был призван внушать ужас мировому империализму – благо, до границы ближайшего «идеологически враждебного государства» от Калининграда было рукой подать. К слову, за проект этого монстра его создатель Юрий Моторин получил Госпремию СССР.", smallDescription: "<<Здание робот>> на центральной площади", latitude: 54.70991, longitude: 20.51264, City: "Kaliningrad, Russia", sightsName: "Дом советов")
        cells.append(Kalin1)
        let Kalin2 = PlaceDataInfo(mainImage: UIImage(named: "Kalin2")!, fullDescription: "Тогда, в апреле 1945 года, здесь располагался штаб коменданта города-крепости Кёнигсберг генерала Отто Ляша. Именно здесь 9 апреля 1945 года в 21 час 30 минут был подписан акт о капитуляции немецкого гарнизона Кёнигсберга.Бункер состоит из 21 помещения, расположенных на глубине 7 метров. Его длина составляет 42 метра, а ширина – 15 метров.", smallDescription: "Экспозиция, размещенная в бывшем немецком бомбоубежище, воссоздает штурм столицы Восточной Пруссии советскими войсками.", latitude: 54.7133, longitude: 20.50956, City: "Kaliningrad, Russia", sightsName: "МУЗЕЙ «БУНКЕР»")
        cells.append(Kalin2)
        let Kalin3 = PlaceDataInfo(mainImage: UIImage(named: "Kalin3")!, fullDescription: "Имя Канта ему дали из-за расположенной на нем могилы великого мыслителя. До 1924 года над ней возвышалась небольшая лютеранская часовня, но она не пережила тех испытаний, что выпали на долю города в середине прошлого века – сейчас на ее месте расположен солидный гранитный мемориал с тринадцатью розовыми порфировыми колоннами, призванный увековечить память великого философа.Кроме могилы Канта, остров известен расположенным на нем Кафедральным собором, бывшим некогда главным храмом столицы Восточной Пруссии.", smallDescription: "В настоящее время небольшой островок, образованный рукавами реки Преголя, носит имя Иммануила Канта, основателя и одного из самых ярких представителей немецкой классической философии, хотя до недавнего времени остров в самом средоточии старого Калининграда официально назывался Центральным.", latitude: 54.70638, longitude: 20.51206, City: "Kaliningrad, Russia", sightsName: "Остров Канта")
        cells.append(Kalin3)
        let Kalin4 = PlaceDataInfo(mainImage: UIImage(named: "Kalin4")!, fullDescription: "Есть даже мнение, что своим названием мост обязан медовому месяцу – на самом деле Медовым он называется из-за взятки в размере бочонка меда, которую пришлось заплатить горожанам муниципалитету за право этот мост построить.", smallDescription: "Эта достопримечательность Калининграда особенно популярна у новобрачных и выпускников (наряду с Юбилейным мостом, что расположен чуть южнее).", latitude: 54.70576, longitude: 20.51382, City: "Kaliningrad, Russia", sightsName: "Медовый мост")
        cells.append(Kalin4)
        let Kalin5 = PlaceDataInfo(mainImage: UIImage(named: "Kalin5")!, fullDescription: "Строить ее начали в 2006 году по инициативе тогдашнего губернатора Калининградской области Павла Федорова, желавшего, видимо, таким образом хоть немного устранить явный дефицит исторических и приятных", smallDescription: "Рыбная деревня Калининграда – стилизованный под «старую Пруссию» этнографический и торговый комплекс, расположенный на правом берегу реки Преголя, между Медовым и Юбилейным мостами.", latitude: 54.70481, longitude: 20.51503, City: "Kaliningrad, Russia", sightsName: "Рыбная деревня")
        cells.append(Kalin5)
        let Kalin6 = PlaceDataInfo(mainImage: UIImage(named: "Kalin6")!, fullDescription: "Церковь Креста была возведена в год прихода нацистов к власти в Германии – 1933, и ей недолго суждено было носить это имя и вообще быть церковью. Политика Гитлера привела Германию к катастрофе, советские войска катком прошлись по Восточной Пруссии, и немногие выжившие в том огне лютеранские кирхи затаились до лучших времен: например, в церкви Креста в послевоенные годы размещался цех по плетению рыбацких сетей. В 1990 году она была переосвящена в православный Крестовоздвиженский собор Калининградской епархии. В том же году в новообретенной церкви установили янтарный алтарь — уникальный по меркам остальной России, но вполне обычный для Янтарного края. Буде вы православной веры, можно поставить в Крестовоздвиженском соборе свечку – и посмотреть, как играет ее пламя на фоне янтарных пластин алтаря.", smallDescription: "В былые времена экстерьер Кройцкирхе украшал не только гигантский белый крест, но и надпись на немецком «Слово о Кресте являет могущество Господа», а также изображения пеликанов – в средневековой Западной Европе они являлись религиозным символом самопожертвования: считалось, что пеликан выкармливает своих птенцов собственной кровью, пока не умирает. ", latitude: 54.7057, longitude: 20.52258, City: "Kaliningrad, Russia", sightsName: "Кирха креста")
        cells.append(Kalin6)
        let Kalin7 = PlaceDataInfo(mainImage: UIImage(named: "Kalin7")!, fullDescription: "Пришлось даже установить на «Павильоне Мюнхгаузена» поясняющую табличку с надписью по-русски и по-немецки: «В этом доме 29 января 1738 года просто не мог ни жить, ни работать, ни творить Карл Фридрих Иероним фон Мюнхгаузен. Тот самый.».", smallDescription: "Вообще-то упомянутое строение является лоцманским домиком и одновременно будкой с подъемными механизмами Высокого моста, но народная молва каким-то образом соединила его с именем великого фантазера и героя сказок.", latitude: 54.6991, longitude: 20.51713, City: "Kaliningrad, Russia", sightsName: "Павильон Мюнхаугазена")
        cells.append(Kalin7)
        let Kalin8 = PlaceDataInfo(mainImage: UIImage(named: "Kalin8")!, fullDescription: "После Великой Отечественной войны внутренние помещения ворот долгое время использовались как склад, пока в 1956 году не было принято решение открыть в них музей, посвященный истории Кёнигсберга XIX-XX веков – основой экспозиции послужили предметы, найденные во время реконструкции Южного парка Калининграда.", smallDescription: "Эти ворота названы в честь прусского города Фридланд (ныне Правдинск) – из всех восьми ворот Кёнигсберга они были построены последними", latitude: 54.69537, longitude: 20.52176, City: "Kaliningrad, Russia", sightsName: "Фридланские Ворота")
        cells.append(Kalin8)
        let Kalin9 = PlaceDataInfo(mainImage: UIImage(named: "Kalin9")!, fullDescription: "Было время, когда вся страна была утыкана памятниками вождям всевозможных калибров, затем маятник истории качнулся в другую сторону и снес собой большинство из них; причем самые великие и одиозные вожди «попадали» в первую очередь. А вот ничем особо не примечательный Михал Иваныч Калинин, известный разве что своей женой-эстонкой, которую Сталин отправил в лагерь, и супруг потом долго упрашивал вождя ее выпустить (достойный уважения поступок, кстати: не отказался Михал Иваныч от нее, не отрекся. А времена тогда крутые были, далеко не все из попадавших в подобный переплет выбирали похожую линию поведения) – так вот, невзрачный Калинин и на постаменте устоял, и город его имени так и остался на карте мира.", smallDescription: "Иногда эту площадь (бывшую Рейсхплац) еще называют Привокзальной.", latitude: 54.69564, longitude: 20.49908, City: "Kaliningrad, Russia", sightsName: "Площадь Калинина")
        cells.append(Kalin9)
        let Kalin10 = PlaceDataInfo(mainImage: UIImage(named: "Kalin10")!, fullDescription: "Бранденбургские ворота пережили сокрушительный штурм Кёнигсберга советскими войсками в апреле 1945 года и на данный момент являются одной из немногих уцелевших «прусских» достопримечательностей Калининграда — причем пережили они его столь удачно, что до сих пор выполняют свою первоначальную функцию: через них проходит мощеная булыжником автомобильная дорога, дополненная трамвайными путями.", smallDescription: "Бранденбургские ворота пережили сокрушительный штурм Кёнигсберга советскими войсками в апреле 1945 года и на данный момент являются одной из немногих уцелевших «прусских» достопримечательностей Калининграда", latitude: 54.69537, longitude: 20.52176, City: "Kaliningrad, Russia", sightsName: "Браденбурские ворота")
        cells.append(Kalin10)
        let Kazan1 = PlaceDataInfo(mainImage: UIImage(named: "Kazan1")!, fullDescription: "После недолгих дебатов ученые пришли к выводу, что первые поселения на территории Казанского Кремля относятся к рубежу X-XI в, возраст Кремля отсчитывают с этих пор. Вход на территорию, ограниченную почти двухкилометровой стеной, свободный.На территории Кремля находятся: Башня Сююмбике,Благовещенский собор,Закладной камень мечети Кул Шариф,Комплекс Пушечный двор,Мечеть Кул Шариф,Памятник зодчим Казанского кремля,Обзорные площадки", smallDescription: "Расположенная в историческом центре на холме крепость, имеющая статус музея-заповедника. Объект Всемирного наследия ЮНЕСКО.", latitude: 55.796435, longitude: 49.108244, City: "Kazan, Tatarstan, Russia", sightsName: "Казанский кремль")
        cells.append(Kazan1)
        let Kazan2 = PlaceDataInfo(mainImage: UIImage(named: "Kazan2")!, fullDescription: "Интересна не только для любителей пеших прогулок. Помимо скульптур и сувениров, интерес представляют фонтаны, в частности, фонтан Водяная <<Су Анасы>>, установленный в 1997 году. Героиню татарской сказки <<Су Анасы>> часто путают с русалкой.", smallDescription: "Эта достопримечательность Казани сродни Старому Арбату в Москве. Пешеходная улица в центре города, где можно побродить, посмотреть, перекусить, если проголодался.", latitude: 55.7882975, longitude: 49.1175847, City: "Kazan, Tatarstan, Russia", sightsName: "Улица Баумана")
        cells.append(Kazan2)
        let Kazan3 = PlaceDataInfo(mainImage: UIImage(named: "Kazan3")!, fullDescription: "Построен в 1723-1726 г.г. и посвящен небесным покровителям Петра Великого Апостолам Петру и Павлу. Строительство было связано с приездом в Казань 27 мая 1722 года Петра Первого.", smallDescription: "Красивейшая архитектурная достопримечательность Казани.", latitude: 55.7935018, longitude: 49.1109911, City: "Kazan, Tatarstan, Russia", sightsName: "Петропаловский собор")
        cells.append(Kazan3)
        let Kazan4 = PlaceDataInfo(mainImage: UIImage(named: "Kazan4")!, fullDescription: "Трудовые мигранты семейства кошачьих помогли извести грызунов в царских покоях.В память о событиях тех дней и появились эти два оригинальных достопримечательности-коты-памятники. Один из них теперь находится на улице Баумана, а второй был открыт за несколько месяцев до этого на берегу озера в Раифском монастыре.", smallDescription: "Некогда по приказу Екатерины II, 30 казанских котов были перевезены в Санкт-Петербург. Произошло это после визита Екатерины в Казань, когда она заметила отсутствие мышей в городе.", latitude: 55.7923142, longitude: 49.1113364, City: "Kazan, Tatarstan, Russia", sightsName: "Памятник казанскому коту")
        cells.append(Kazan4)
        let Kazan5 = PlaceDataInfo(mainImage: UIImage(named: "Kazan5")!, fullDescription: "Здание двухэтажное, состоит из четырёх объединенных домов. На углу высится полукруглый эркер, окружённый балконом с ажурной решеткой, стены дома декорированы пышной лепниной.Внутренняя отделка, так же как и фасады, выполнена с большой роскошью. Интерьеры выдержаны в разных стилях: ампир, «готическом», «мавританском», «китайском» и др. Помещения первого этажа покрывает золочёная лепнина, парадная лестница выполнена в псевдовосточном стиле. Здесь много скульптур из бронзы и дерева. Окна покрывает витраж с изображением экзотических птиц парижской работы. Главный зал отделан в стиле ампир, модного во Франции в первой трети XIX века. Потолок зала выполнен в виде ромба с пышной лепниной. Стены столовой из морёного дуба, по сторонам есть хоры для оркестра. Еще одной достопримечательностью этого здания является одно из помещений, оформленное как грот, в виде пещеры с аквариумом. Кованные решётки выполнены на Петербургском заводе Сан-Гали.", smallDescription: "Дом Зинаиды Ушковой построен по заказу купца и промышленника, владельца Бондюжского химического завода П.К.Ушкова, передавшего его своей сестре Зинаиде. Сегодня в доме Ушковой расположена одна из крупнейших библиотек Татарстана - Национальная библиотека РТ.", latitude: 55.7916437, longitude: 49.118908, City: "Kazan, Tatarstan, Russia", sightsName: "Дом Ушковой")
        cells.append(Kazan5)
        let Kazan6 = PlaceDataInfo(mainImage: UIImage(named: "Kazan6")!, fullDescription: "Прямоугольное в плане здание с двумя закругленными углами и системой внутренних дворов. В оформлении фасадов здания использовались элементы архитектуры на основе стилевых систем ренессанса, барокко и классицизма с обилием лепных украшений растительного характера.В 1890 владелица пассажа О.С.Александрова-Гейнс подарила его городу для размещения музея. Однако из-за трудностей в приспособлении здания такого типа под музей от этой затеи отказались, и здание использовалось по первоначальному назначению. В 1977 северный угол здания обрушился, после реконструкции сохранившейся части в 1980 году в нем размещены административные и торговые учреждения", smallDescription: "Дата постройки - 1883-1886 года, архитекторы - В.В.Суслова и Н.И.Поздеева. Доходный дом пассажного типа с торговыми и зрелищными учреждениями, внутренним двором (пассажем), имеющим стеклянное покрытие.", latitude: 55.7942337, longitude: 49.1133231, City: "Kazan, Tatarstan, Russia", sightsName: "Александровский пассаж")
        cells.append(Kazan6)
        let Kazan7 = PlaceDataInfo(mainImage: UIImage(named: "Kazan7")!, fullDescription: "Изменение русла Казанки,строительство дамбы и повышение уровня вод, привело к тому, что сооружение оказалось на островке. Необычна форма. Интересно содержание. После заключения найденных мощей в бетонный саркофаг, была открыта дополнительно церковь-усыпальница в честь преподобного Илии Муромца(под храмом,2006г.). Все это неоднократно видоизменялось по ряду деталей, переименовывалось, разграблялось, осквернялось. Освящалось еще два раза. Поэтому, попасть крайне сложно. Богослужения два раза в год-19 января(Крещение Господне) и 29 августа- отмечается Водосвятие(Третий Спас).", smallDescription: "Храм-Памятник русским воинам, павшим при взятии Казани в 1552 году.Начиналось, как захоронение/курган/братская могила,на берегу Казанки. Позже, по ряду причин,было перенесено немного дальше. Затем, был заложен,возведен и освящен храм( во имя Нерукотворного образа Спасителя)-1823г.", latitude: 55.7597583, longitude: 48.6738217, City: "Kazan, Tatarstan, Russia", sightsName: "Памятник войнам")
